<?phpclass DBAccess{//=========================================================================	public static function query($iQuery)	{		$db = new SQLite3("auto.sqlite");		$dbResult = $db->query($iQuery);		$result = array();		while($row = $dbResult->fetchArray(SQLITE3_ASSOC))		{			$utf8row = array();			foreach($row as $key => $val)			{				$utf8row[$key] = utf8_encode($val);			}			$result[] = $utf8row;		}		return $result;	}//=========================================================================	public static function querySingle($iQuery)	{		$db = new SQLite3("auto.sqlite");		$row = $db->querySingle($iQuery, true);		$utf8row = array();		foreach($row as $key => $val)		{			$utf8row[$key] = utf8_encode($val);		}		return $utf8row;	}//=========================================================================	public static function exec($iQuery)	{		$db = new SQLite3("auto.sqlite");		return $db->exec($iQuery);	}}function getImage($iImageName){	if(is_file("../" . $iImageName . ".jpg")) return $iImageName . ".jpg";	if(is_file("../" . $iImageName . ".png")) return $iImageName . ".png";	return $iImageName . ".gif";}?>